import React from 'react';

//Recieve the onRouteChange Prop from App.js
class Register extends React.Component {

    constructor(props){

        super(props)

        this.state = {

            email: '',

            password: '',

            name: ''
        }
    }


    //Get value from name input
    onNameChange = (event) => {

        this.setState({name: event.target.value})
    }


    //Get value from email input
    onEmailChange = (event) => {

        this.setState({email: event.target.value})
    }

    //Get value from password input
    onPasswordChange = (event) => {

        this.setState({password: event.target.value})
    }


    //When the submit Register button is clicked
    onSubmitSignIn = () => {

        //console.log(this.state);

        //Send request to our server 
        fetch('http://localhost:3001/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.email,
                password: this.state.password,
                name: this.state.name
            })
        })
        .then(response => response.json())
        .then(user => {

            if (user) {

                //Load new user
                loadUser()

                //Change the route to home
                this.props.onRouteChange('home') 
            } 
        })


    }


    render(){

        // const { onRouteChange } = this.props

        return (

            <article className="br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">

            <main className="pa4 black-80">
                <div className="measure">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                        <legend className="f1 fw6 ph0 mh0">Register</legend>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name" id="name" onChange={this.onNameChange} />
                        </div>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address" id="email-address" onChange={this.onEmailChange} />
                        </div>
                        <div className="mv3">
                            <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                            <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password" id="password" onChange={this.onPasswordChange} />
                        </div>
                        {/* <label className="pa0 ma0 lh-copy f6 pointer"><input type="checkbox" /> Remember me</label> */}
                    </fieldset>
                    <div className="">
                        <input onClick={ this.onSubmitSignIn } className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Register" />
                    </div>
                </div>
            </main>

            </article>

        );
    }


}

export default Register;
